	// demonstrate JavaScript Closure with a getnum and setnum
	var number = 7;

	function numberFunction(){
		var number = 10;
		return{
			getNumber: function(){
				return 'Number is: ' + number;
			},
			setNumber: function(newNumber){
				number = newNumber;
				return 'Number is now: ' + number;
			}
		};
	}
	var callFunction = numberFunction();
	callFunction.getNumber();
	callFunction.setNumber(2);
	callFunction.getNumber();
	numberFunction.getNumber();
})(current, previous);
