sys_script_include.script:
var GetEmailAddress = Class.create();
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	// a value for sysParameter_userID must be passed into this Script Includes
	// create an empty GlideRecord for the User table sys_user, populate it using the userID, return email address
	getEmail: function(){
		var userRecord = new GlideRecord('sys_user');
		userRecord.get(this.getParameter('systemParameter_userID'));
		return String(userRecord.email);
	},
    type: 'GetEmailAddress'
});
