sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	// create function to determine if a date is in the past
	isDatePast: function(dateString){
		var rightNow = new GlideDateTime();
		var date = new GlideDateTime(dateString);
		// return a bool indicating the result of our test
		if(date.before(rightNow)){
			return true;
		}
		else{
			return false;
		}
	},

	// create function to determine if a date is today
	isDateToday: function(dateString){
		var rightNow = new GlideDateTime();
		var date = new GlideDateTime(dateString);
		// return a bool indicating the result of our test
		if(date.getDate() == rightNow.getDate()){
				return true;
			}
		else{
			return false;
		}
	},

    type: 'NeedItUtils'
};
