sys_script_include.script:
function validateEmailAddress(emailString){
	// use JavaScript type coercion to guarantee emailString is a String
	emailString = String(emailString);

	// compare emailString to allowed syntax using regex and return true or false based on validity
	if(emailString.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
		return true;
	}
	else{
		return false;
	}
}
